// src/components/Sidebar.js

import React from 'react';
import { List, ListItem, ListItemText, Divider, Drawer, Typography } from '@mui/material';
import { Link } from 'react-router-dom';

const drawerWidth = 240;

const Sidebar = () => {
  return (
    <Drawer variant="permanent" anchor="left" sx={{ width: drawerWidth, flexShrink: 0 }}>
      <div style={{ width: drawerWidth, backgroundColor: '#1976d2', color: 'white', padding: '20px' }}>
        <Typography variant="h6" noWrap>
          My Dashboard
        </Typography>
      </div>
      <Divider />
      <List>
        {['Control', 'Data', 'Teams'].map((text, index) => (
          <ListItem 
            button 
            key={text} 
            component={Link} 
            to={index === 0 ? '/' : `/${text.toLowerCase()}`} 
            sx={{ '&:hover': { backgroundColor: '#f1f1f1' }}}
          >
            <ListItemText primary={text} sx={{ color: '#1976d2' }} />
          </ListItem>
        ))}
      </List>
      <Divider />
    </Drawer>
  );
};

export default Sidebar;
